<script>
import axios from 'axios';
import sha1 from 'js-sha1';

const {phone} = require('phone');

export default {
  name: "index.vue",
  computed: {
    urlHash() {
      return this.$route.hash || ''
    },
  },
  watch: {
    urlHash() {
      this.onUrlHashChanged();
    },

    cardId(value) {
      if (value) {
        this.onCardIdChanged();
      }
    }
  },
  methods: {
    clearHashFromUrl() {
      history.replaceState(null, null, ' ');
    },
    clearCardInfo() {
      this.cardInfo = null;
      this.cardId = '';
      this.emailAddress = '';
      this.phoneNumber = '';
    },
    async onUrlHashChanged() {
      let shortCardId = this.urlHash.match(/^#\/([0-9a-f]{10})$/);
      shortCardId = shortCardId && shortCardId[1] || '';
      let cardId = '';

      if (shortCardId) {
        try {
          let response = await axios.get(`https://ducati-israel-vcard.s3.eu-central-1.amazonaws.com/short/${shortCardId}.json`)
          cardId = response.data.id
        } catch (e) {
          console.error('failed resolving short card id', e)
        }
      }

      if (!cardId) {
        cardId = this.urlHash.match(/^#\/([0-9a-f]{40})$/);
        cardId = cardId && cardId[1] || '';
      }

      if (cardId) {
        this.cardId = cardId;
      }
    },

    async onCardIdChanged() {
      if (!this.cardId) {
        return;
      }
      this.loading = true;

      try {
        let response = await axios.get(`https://ducati-israel-vcard.s3.eu-central-1.amazonaws.com/card/${this.cardId}.json`)
        this.cardInfo = response.data
      } catch (e) {
        console.error(e);
      } finally {
        this.loading = false;
      }
    },

    async submitForm() {
      let phoneNumber = this.phoneNumber;
      let normalizedPhoneNumber = phone(phoneNumber, {country: 'IL'});
      if (normalizedPhoneNumber && normalizedPhoneNumber.isValid) {
        phoneNumber = normalizedPhoneNumber.phoneNumber;
      }

      let emailAddress = this.emailAddress;
      let cardId = sha1(`${emailAddress}:${phoneNumber}`)
      this.cardId = cardId;
    },
  },
  mounted() {
    this.onUrlHashChanged();
    this.onCardIdChanged();
  },

  data() {
    return {
      cardId: '',
      emailAddress: '',
      phoneNumber: '',
      error: '',
      loading: false,
      cardInfo: null,
    }
  }
}
</script>

